import {
    IonContent,
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonBackButton,
    IonInput,
    IonButton,
    IonList,
    IonItem,
    IonTextarea,
    IonDatetime,
    IonLabel,
  } from '@ionic/react';
  import React, { useState } from 'react';
  import { useHistory } from 'react-router';
  import { useAuth } from '../auth';
  import { firestore } from '../firebase';

  const AddEntryPage: React.FC = () => {
    const [date, setDate] = useState<string>('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const { userId } = useAuth();
    const history = useHistory();
    const [pictureUrl, setPictureUrl] = useState('/assets/placeholder.png');


    const handleFileChange = (event: React.ChangeEvent ) => {
      console.log('Files:', event.target.file);
    };

    const handleSave = async () => {
      const entriesRef = firestore.collection("users").doc(userId).collection("entries");
      const entryData = { date, title, description };
      const entryRef = await entriesRef.add(entryData);
      console.log('saved', entryRef.id);
      // .add({
      //   title: title,
      //   description: description,
      //   date: setDate
      // });
      history.goBack();
    }

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonButtons slot="start">
              <IonBackButton />
            </IonButtons>
            <IonTitle> Add Entry </IonTitle>
          </IonToolbar>
        </IonHeader>

        <IonContent className="ion-padding">
          <IonList>
          <IonItem>
              <IonLabel position="floating">Date </IonLabel>
              <IonDatetime value={date} onIonChange={(e) => setDate(e.detail.value)} />
            </IonItem>

            <IonItem>
              <IonLabel position="floating">Title </IonLabel>
              <IonInput type="text" value={title} onIonChange={(e) => setTitle(e.detail.value)} />
            </IonItem>

            <IonItem>
              <IonLabel position="floating">Picture</IonLabel><br /><br />
              <input type="file" accept="image/*"/>
              <img src="{pictureUrl}" alt=""/>
            </IonItem>

            <IonItem>
              <IonLabel position="floating">Description </IonLabel>
              <IonTextarea value={description} onIonChange={(e) => setDescription(e.detail.value)} />
            </IonItem>

          </IonList>

          <IonButton expand="block" type="submit" onClick={handleSave} routerLink="/my/entries"> Submit </IonButton>

        </IonContent>
      </IonPage >
    );
  };

  export default AddEntryPage;
