import {
    IonContent,
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonBackButton,
    IonButtons,
    IonButton,
    IonInput,
    IonLabel,
    IonTextarea,
    IonDatetime,
    IonItem,
  } from '@ionic/react';
  import React, { useEffect, useState } from 'react';
  import { useHistory, useRouteMatch } from 'react-router';
  import { useAuth } from '../auth';
  import { firestore } from '../firebase';
  import { Entry, toEntry } from '../models';

  interface RouteParams {
    id: string;
  }

  const EditEntryPage: React.FC = () => {
    const match = useRouteMatch<RouteParams>();
    const { id } = match.params;
    const { userId } = useAuth();
    const [entry, setEntry] = useState<Entry>();
    const [title, setTitle] = useState<string>();
    const [description, setDescription] = useState<string>();
    const [date, setDate] = useState<string>();
    const [pictureUrl, setPictureUrl] = useState<string>();
    const history = useHistory();
    useEffect(() => {
      const entryRef = firestore.collection('users').doc(userId)
        .collection('entries').doc(id);
      entryRef.get().then((doc) => { setEntry(toEntry(doc)); });
    }, [userId, id]);

    const handleSave = async () => {
      const entryRef = firestore.collection('users').doc(userId)
        .collection('entries').doc(id);
      await entryRef.update({
        title,
        description,
        date,
        pictureUrl,
      });
      history.goBack();
    };

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonButtons slot="start">
              <IonBackButton />
            </IonButtons>
            <IonTitle>Edit Entry</IonTitle>
          </IonToolbar>
        </IonHeader>
        <IonContent className="ion-padding">
          <IonItem>
            <IonLabel position="floating">Title</IonLabel>
            <IonInput value={title} onIonChange={(e) => setTitle(e.detail.value!)} />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Description</IonLabel>
            <IonTextarea value={description} onIonChange={(e) => setDescription(e.detail.value!)} />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Date</IonLabel>
            <IonDatetime displayFormat="MM/DD/YYYY" value={date} onIonChange={(e) => setDate(e.detail.value!)} />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Picture URL</IonLabel>
            <IonInput value={pictureUrl} onIonChange={(e) => setPictureUrl(e.detail.value!)} />
          </IonItem>
          <IonButton onClick={handleSave}>Save</IonButton>
        </IonContent>
      </IonPage>
    );
  };

  export default EditEntryPage;
