import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction'; // import interaction plugin
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import './calendarstyle.css';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const [addingEntry, setAddingEntry] = useState(false);
  const history = useHistory();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');

    // Listen to changes in the collection and update the entries state
    const unsubscribe = entriesRef.onSnapshot(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });

    return unsubscribe; // Cleanup the listener when the component unmounts

  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date), // convert Firestore Timestamp to JavaScript Date
    id: entry.id, // add the entry id to the event object
    backgroundColor: entry.completed ? 'blue' : 'green', // set the event background color based on completion status
    borderColor: entry.completed ? 'darkblue' : 'darkgreen', // set the event border color based on completion status
  }));

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`); // redirect to the corresponding entry page
  };

  const handleDateClick = (arg: any) => {
    const date = arg.date.toISOString(); // convert the date to a string that can be stored in Firestore
    const newEntry = { title: '', date, content: '', completed: false };
    setAddingEntry(true);
    firestore
      .collection('users')
      .doc(userId)
      .collection('entries')
      .add(newEntry)
      .then(() => {
        setAddingEntry(false);
      })
      .catch((error) => {
        console.error('Error adding entry: ', error);
        setAddingEntry(false);
      });
  };

  const handleSelect = (arg: any) => {
    console.log(arg); // log the selected date range
  };

  return (
    <CalendarContainer>
      <FullCalendar
        plugins={[dayGridPlugin, interactionPlugin]} // add the interaction plugin
        initialView="dayGridMonth"
        events={events}
        eventClick={handleEventClick}
        dateClick={handleDateClick}
        selectable={true} // enable date selection
        select={handleSelect} // specify the function to be executed when a date range is selected
      />
    </CalendarContainer>
  );
};

export default Calendar;
