import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { IonCard, IonCardHeader, IonCardSubtitle, IonCardTitle, IonCardContent } from '@ionic/react';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');
    entriesRef.get().then(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });
  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date), // convert Firestore Timestamp to JavaScript Date
    id: entry.id // add the entry id to the event object
  }));

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`); // redirect to the corresponding entry page
  };

  const handleDayRender = (info: any) => {
    const date = info.date.toDateString();
    const dayEntries = entries.filter((entry) => new Date(entry.date).toDateString() === date);
    const cards = dayEntries.map((entry) => (
      <IonCard key={entry.id} onClick={() => history.push(`/my/entries/view/${entry.id}`)}>
        <IonCardHeader>
          <IonCardSubtitle>{entry.date().toLocaleDateString()}</IonCardSubtitle>
          <IonCardTitle>{entry.title}</IonCardTitle>
        </IonCardHeader>
        <IonCardContent>{entry.description}</IonCardContent>
      </IonCard>
    ));
    info.el.innerHTML += cards.join('');
  };

  return (
    <CalendarContainer>
      {!loading && (
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={events}
          eventClick={handleEventClick} // attach the click event handler
          dayRender={handleDayRender} // render custom content for each day cell
          lazyFetching={true} // fetch events for the visible range of dates
          height='450px'
        />
      )}
    </CalendarContainer>
  );
};

export default Calendar;
