import {
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonItem,
  IonPage,
  IonTitle,
  IonToolbar,
  IonLabel,
  IonThumbnail,
  IonImg,
  IonList,
  IonItemSliding,
  IonText,
  IonItemOptions,
  IonItemOption,
  IonCard,
  IonCheckbox,
  IonToggle,
} from '@ionic/react';
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { add as addIcon, checkmarkCircleOutline, handLeft } from 'ionicons/icons';
import { formatDate } from '../date';

const HomePage: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [showCompleted, setShowCompleted] = useState(false);

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries')
      .orderBy('date', 'desc');

    return entriesRef.onSnapshot(({ docs }) => setEntries(docs.map(toEntry)));
  }, [userId]);

  const handleComplete = (entry: Entry) => {
    const entryRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries')
      .doc(entry.id);

    entryRef
      .update({
        completed: true,
      })
      .then(() => {
        console.log('Entry updated successfully!');
      })
      .catch((error) => {
        console.error('Error updating entry: ', error);
      });
  };

  // filter entries based on showCompleted state
  const filteredEntries = showCompleted ? entries : entries.filter((entry) => !entry.completed);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Homepage</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonList>
          <IonItem>
            <IonLabel>Show Completed Entries</IonLabel>
            <IonToggle checked={showCompleted} onIonChange={(e) => setShowCompleted(e.detail.checked)} />
          </IonItem>
        </IonList>
        {filteredEntries.map((entry) => (
          <React.Fragment key={entry.id}>
            {entry.completed ? (
              <IonItem routerLink={`/my/entries/view/${entry.id}`}>
                <IonText>
                  <h2>{entry.title}</h2>
                  <h6>{formatDate(entry.date)}</h6>
                </IonText>
              </IonItem>
            ) : (
              <IonItemSliding>
                <IonItem routerLink={`/my/entries/view/${entry.id}`}>
                  <IonText>
                    <h2>{entry.title}</h2>
                    <h6>{formatDate(entry.date)}</h6>
                  </IonText>
                </IonItem>
                <IonItemOptions side="end">
                  <IonItemOption color="success" onClick={() => handleComplete(entry)}>
                    <IonIcon slot="icon-only" icon={checkmarkCircleOutline} />
                  </IonItemOption>
                </IonItemOptions>
              </IonItemSliding>
            )}
          </React.Fragment>
        ))}
        <IonFab vertical="bottom" horizontal="end" slot="fixed">
          <IonFabButton routerLink="/my/entries/add">
            <IonIcon icon={addIcon} />
          </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );

};

export default HomePage;
