import {
  IonButton,
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonCard,
  IonCardContent,
  IonItem,
  IonLabel,
  IonIcon,
  IonAlert,
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { auth, firestore } from '../firebase';
import { mailOutline, trashOutline } from 'ionicons/icons';

const SettingsPage: React.FC = () => {
  const [userEmail, setUserEmail] = useState<string | null>(null);
  const [showDeleteAlert, setShowDeleteAlert] = useState(false);

  useEffect(() => {
    // Get the currently logged in user's email
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        setUserEmail(user.email);
      }
    });

    return () => {
      unsubscribe();
    };
  }, []);

  const handleDeleteAccount = async () => {
    const user = auth.currentUser;
    if (user) {
      // Delete user data in Firestore if it exists
      const userDocRef = firestore.collection('users').doc(user.uid);
      const userDoc = await userDocRef.get();
      if (userDoc.exists) {
        await userDocRef.delete();
      }
      // Delete user account
      await user.delete();
    }
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Settings</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        {userEmail && (
          <IonCard>
            <IonCardContent>
              <IonItem>
                <IonLabel>Email:</IonLabel>
                <IonLabel>{userEmail}</IonLabel>
              </IonItem>
            </IonCardContent>
          </IonCard>
        )}
        <IonCard>
          <IonCardContent>
            <IonItem button href="mailto:hninwintthuaung@gmail.com">
              <IonIcon icon={mailOutline} slot="start" />
              <IonLabel>Contact Developer</IonLabel>
            </IonItem>
          </IonCardContent>
        </IonCard>
        <IonCard>
          <IonCardContent>
            <IonItem button onClick={() => setShowDeleteAlert(true)}>
              <IonIcon icon={trashOutline} slot="start" />
              <IonLabel>Delete Account</IonLabel>
            </IonItem>
          </IonCardContent>
        </IonCard>
        <IonButton expand="block" onClick={() => auth.signOut()}>
          Logout
        </IonButton>
        <IonAlert
          isOpen={showDeleteAlert}
          header="Delete Account"
          message="Are you sure you want to delete your account? This action is irreversible and all your data will be lost."
          buttons={[
            {
              text: 'Cancel',
              role: 'cancel',
              handler: () => setShowDeleteAlert(false),
            },
            {
              text: 'Delete',
              handler: handleDeleteAccount,
            },
          ]}
        />
      </IonContent>
    </IonPage>
  );
};

export default SettingsPage;
