import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');
    entriesRef.get().then(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });
  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date), // convert Firestore Timestamp to JavaScript Date
    id: entry.id // add the entry id to the event object
  }));

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`); // redirect to the corresponding entry page
  };

  const handleDayCellContent = (arg: any) => {
    const date = arg.date.toISOString().split('T')[0]; // convert the date to ISO format
    const entriesOnDate = entries.filter((entry) => entry.date.toISOString().split('T')[0] === date);
    return (
      <div>
        {entriesOnDate.map((entry) => (
          <div key={entry.id}>
            <div>{entry.title}</div>
            <div>{entry.description}</div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <CalendarContainer>
      {!loading && (
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={events}
          eventClick={handleEventClick}
          dayCellContent={handleDayCellContent} // attach the custom day cell content function
          lazyFetching={true}
          height='450px'
        />
      )}
    </CalendarContainer>
  );
};

export default Calendar;
