import {
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonItem,
  IonPage,
  IonTitle,
  IonToolbar,
  IonLabel,
  IonThumbnail,
  IonImg,
} from '@ionic/react';
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { add as addIcon } from 'ionicons/icons';
import { formatDate } from '../date';
import './HomePage.css'
import Hammer from 'hammerjs'
const HomePage: React.FC = () => {
  const { userId } = useAuth();
  const [ entries, setEntries ] = useState<Entry[]>([]);
  // const entryRef = firestore.collection('entries').doc(id);
  // entryRef.get().then((doc) => setEntry(toEntry(entry)));

  useEffect(() => {
      const entriesRef = firestore.collection('users').doc(userId)
        .collection('entries');
      return entriesRef.orderBy('date','desc').limit(7)
        .onSnapshot(({docs}) => setEntries(docs.map(toEntry)))
      //  entriesRef.get().then(({docs}) => setEntries(docs.map(toEntry)))
  }, [userId]);

  // Get all the ion-items on the page
const ionItems = document.querySelectorAll('ion-item');

// Loop through the ion-items and add the swipe-to-delete option
ionItems.forEach((item) => {
  const swipeElement = item.querySelector('.item-wrapper');
  const deleteButton = document.createElement('ion-button');
  deleteButton.innerHTML = 'Delete';
  deleteButton.slot = 'end';
  deleteButton.color = 'danger';
  deleteButton.onclick = () => {
    // Delete the item from the database
    const entryRef = firestore.collection('users').doc(userId)
      .collection('entries').doc(item.getAttribute('id'));
    entryRef.delete();
  };
  const swipeHandler = new Hammer(swipeElement);
  swipeHandler.on('swipeleft', () => {
    item.classList.add('ion-item-sliding');
    item.querySelector('ion-item-options').classList.add('ion-activated');
  });
  swipeHandler.on('swiperight', () => {
    item.classList.remove('ion-item-sliding');
    item.querySelector('ion-item-options').classList.remove('ion-activated');
  });
  item.querySelector('ion-item-options').appendChild(deleteButton);
});


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Homepage</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        {entries.map((entry) =>
          <IonItem button key={entry.id}
            routerLink={`/my/entries/view/${entry.id}`}>
            <IonThumbnail slot="end">
              <IonImg src={entry?.pictureUrl}/>
            </IonThumbnail>
            <IonLabel>
              <h1>{formatDate(entry.date)}</h1>
              <h2>{entry.title}</h2>
            </IonLabel>
          </IonItem>
        )}

        <IonFab vertical="bottom" horizontal="end">
            <IonFabButton routerLink="/my/entries/add">
              <IonIcon icon={addIcon} />
            </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default HomePage;
