import React, { useEffect, useState } from 'react';
import { useHistory, useLocation, useParams } from 'react-router';
import {
  IonButton,
  IonButtons,
  IonContent,
  IonDatetime,
  IonHeader,
  IonInput,
  IonItem,
  IonLabel,
  IonPage,
  IonTextarea,
  IonTitle,
  IonToolbar,
  IonBackButton,
} from '@ionic/react';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { formatDate } from '../date';

interface RouteParams {
  id: string;
}

const EditEntryPage: React.FC = () => {
  const { id } = useParams<RouteParams>();
  const { userId } = useAuth();
  const history = useHistory();
  const location = useLocation<{ entry: Entry }>();

  // State variables to hold entry information
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState(formatDate(new Date()));
  const [pictureUrl, setPictureUrl] = useState('');
  const [link, setLink] = useState('');

  useEffect(() => {
    // Check if entry is being edited or added
    if (location.state?.entry) {
      // If editing, populate fields with existing entry information
      const { title, description, date, pictureUrl, link } = location.state.entry;
      setTitle(title);
      setDescription(description);
      setDate(formatDate(new Date(date)));
      setPictureUrl(pictureUrl);
      setLink(link);
    } else {
      // If adding, get empty entry from Firestore
      const entryRef = firestore.collection('users').doc(userId)
        .collection('entries').doc(id);
      entryRef.get().then((doc) => {
        const { title, description, date, pictureUrl, link } = toEntry(doc);
        setTitle(title);
        setDescription(description);
        setDate(formatDate(new Date(date)));
        setPictureUrl(pictureUrl);
        setLink(link);
      });
    }
  }, [id, location.state?.entry, userId]);

  const handleSave = async () => {
    // Save entry to Firestore with updated information
    const entryRef = firestore.collection('users').doc(userId)
      .collection('entries').doc(id);
    const updatedEntry = {
      title,
      description,
      date: formatDate(new Date(date)),
      pictureUrl,
      link
    };
    await entryRef.set(updatedEntry, { merge: true });

    // Redirect to view entry page and show success message
    history.push(`/my/entries/view/${id}`, { successMessage: 'Entry updated successfully' });
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonButtons slot="start">
            <IonBackButton />
          </IonButtons>
          <IonTitle>{location.state?.entry ? 'Edit Entry' : 'Add Entry'}</IonTitle>
          <IonButtons slot="end">
            <IonButton onClick={handleSave}>Save</IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        <IonItem>
          <IonLabel position="floating">Title</IonLabel>
          <IonInput
            value={title}
            onIonChange={(e) => setTitle(e.detail.value!)}
          />
        </IonItem>
        <IonItem>
          <IonLabel position="floating">Description</IonLabel>
          <IonTextarea
            value={description}
            onIonChange={(e) => setDescription(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Link</IonLabel>
            <IonTextarea
              value={link}
              onIonChange={(e) => setLink(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Date</IonLabel>
            <IonDatetime
              display-format="MMM DD, YYYY HH:mm"
              picker-format="MMM DD YYYY HH:mm"
              value={date}
              onIonChange={(e) => setDate(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Picture URL</IonLabel>
            <IonInput
              value={pictureUrl}
              onIonChange={(e) => setPictureUrl(e.detail.value!)}
            />
            </IonItem>
      </IonContent>
    </IonPage>
  );
};

export default EditEntryPage;
