import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { IonCard, IonCardContent } from '@ionic/react';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();
  const timestamp = firebase.firestore.Timestamp.fromDate(new Date());
  const date = dateTimestamp.toDate();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');
    entriesRef.get().then(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });
  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date), // convert Firestore Timestamp to JavaScript Date
    id: entry.id // add the entry id to the event object
  }));

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`); // redirect to the corresponding entry page
  };

  const dayHeaderContent = (args: any) => {
    const selectedDate = args.date;
    const entriesOnDate = entries.filter((entry) => {
      const entryDate = new Date(entry.date.toDate());
      return (
        entryDate.getFullYear() === selectedDate.getFullYear() &&
        entryDate.getMonth() === selectedDate.getMonth() &&
        entryDate.getDate() === selectedDate.getDate()
      );
    });

    return (
      <>
        <div>{args.dayNumberText}</div>
        {entriesOnDate.map((entry) => (
          <IonCard
            key={entry.id}
            onClick={() => history.push(`/my/entries/view/${entry.id}`)}
          >
            <IonCardContent>
              <h3>{entry.title}</h3>
              <p>{entry.description}</p>
            </IonCardContent>
          </IonCard>
        ))}
      </>
    );
  };

  return (
    <CalendarContainer>
      {!loading && (
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={events}
          eventClick={handleEventClick} // attach the click event handler
          lazyFetching={true} // fetch events for the visible range of dates
          height='450px'
          dayHeaderContent={dayHeaderContent} // add the custom content to the day header
        />
      )}
    </CalendarContainer>
  );
};

export default Calendar;
