import {
    IonContent,
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonBackButton,
    IonInput,
    IonButton,
    IonList,
    IonItem,
    IonTextarea,
    IonDatetime,
    IonLabel
  } from '@ionic/react';
  import { Plugins } from '@capacitor/core';
  import React, { useEffect, useRef, useState } from 'react';
  import { useAuth } from '../auth';
  import { firestore, store } from '../firebase';
  import { isPlatform, getPlatforms  } from '@ionic/react';
  import { useHistory, useParams } from 'react-router';

  console.log(isPlatform('ios')); // returns true when running on a iOS device
  console.log(getPlatforms()); // returns ["iphone", "ios", "mobile", "mobileweb"] from an iPhone

  const EditEntryPage: React.FC = () => {
    const [date, setDate] = useState<string>('');
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [link, setLink] = useState('');

    const { entryId } = useParams<{ entryId: string }>();
    const { userId } = useAuth();
    const [pictureUrl, setPictureUrl] = useState('');
    const fileInputRef = useRef<HTMLInputElement>();
    const history = useHistory();

    async function savePicture(blobUrl, userId){
      const pictureRef = store.ref(`/users/${userId}/pictures/${Date.now()}`);
      const response = await fetch(blobUrl);
      const blob = await response.blob();
      const snapshot = await pictureRef.put(blob);
      const url = await snapshot.ref.getDownloadURL();
      //console.log("Saved:", url);
      return url;
    }

    useEffect(() => {
      async function loadEntry() {
        const entryRef = firestore.collection("users").doc(userId).collection("entries").doc(entryId);
        const entrySnapshot = await entryRef.get();
        if(entrySnapshot.exists) {
          const entryData = entrySnapshot.data();
          setDate(entryData.date);
          setTitle(entryData.title);
          setDescription(entryData.description);
          setLink(entryData.link);
          setPictureUrl(entryData.pictureUrl);
        }
      }
      loadEntry();
    }, [entryId, userId]);

    useEffect(() => () => {
      if(pictureUrl.startsWith('blob')) {
        URL.revokeObjectURL(pictureUrl);
        console.log('Revoked', pictureUrl); 
      }
    }, [pictureUrl]);

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      console.log('Files:', event.target.files);
      if(event.target.files.length > 0) {
        const file = event.target.files.item(0);// get the file
        const pictureUrl = URL.createObjectURL(file); // URL object extracted from the file
        // console.log('created: ', pictureUrl);
        setPictureUrl(pictureUrl);
      }
    };

    const handleSave = async () => {
      const entryRef = firestore.collection("users").doc(userId).collection("entries").doc(entryId);
      const entryData = { date, title, pictureUrl, description, link };
      if(pictureUrl.startsWith('blob')) {
        entryData.pictureUrl = await savePicture(pictureUrl, userId);
      }
      await entryRef.set(entryData, { merge: true });
      console.log('saved', entryRef.id);
      history.goBack();
    }

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonButtons slot="
start">
              <IonBackButton />
            </IonButtons>
            <IonTitle>{location.state?.entry ? 'Edit Entry' : 'Add Entry'}</IonTitle>
            <IonButtons slot="end">
              <IonButton onClick={handleSave}>
                Save
              </IonButton>
            </IonButtons>
          </IonToolbar>
        </IonHeader>
        <IonContent className="ion-padding">
          <IonItem>
            <IonLabel   position="floating">Title</IonLabel>
            <IonInput
              value={title}
              onIonChange={(e) => setTitle(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Description</IonLabel>
            <IonTextarea
              value={description}
              onIonChange={(e) => setDescription(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Link</IonLabel>
            <IonTextarea
              value={link}
              onIonChange={(e) => setLink(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Date</IonLabel>
            <IonDatetime
              display-format="MMM DD, YYYY HH:mm"
              picker-format="MMM DD YYYY HH:mm"
              value={date}
              onIonChange={(e) => setDate(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Picture URL</IonLabel>
            <IonInput
              value={pictureUrl}
              onIonChange={(e) => setPictureUrl(e.detail.value!)}
            />
            </IonItem>
      </IonContent>
    </IonPage>
  );
};

export default EditEntryPage;
