export interface Entry {
    id: string;
    title: string;
    description: string;
}

export function toEntry(doc): Entry{
    return { id: doc.id, ...doc.data() }
}

export function toEntries(snapshot){
  return snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data(), }))
}
