import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonContent } from '@ionic/react';

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [selectedDate, setSelectedDate] = useState<string | null>(null);

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');
    entriesRef.get().then(({ docs }) => setEntries(docs.map(toEntry)));
  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date), // convert Firestore Timestamp to JavaScript Date
  }));

  const handleDateSelect = (arg: { date: DateStr }): void => {
    setSelectedDate(arg.date);
  };

  const tasksForSelectedDate = entries.filter(
    (entry) => entry.date === selectedDate
  );

  return (
    <>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        selectable={true}
        select={handleDateSelect}
      />
      {selectedDate && (
        <IonContent>
          {tasksForSelectedDate.length > 0 ? (
            tasksForSelectedDate.map((task) => (
              <IonCard key={task.id}>
                <IonCardHeader>
                  <IonCardTitle>{task.title}</IonCardTitle>
                </IonCardHeader>
                <IonCardContent>{task.content}</IonCardContent>
              </IonCard>
            ))
          ) : (
            <IonCard>
              <IonCardContent>No tasks for selected date</IonCardContent>
            </IonCard>
          )}
        </IonContent>
      )}
    </>
  );
};

export default Calendar;
