import {
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonItem,
  IonPage,
  IonTitle,
  IonToolbar,
  IonLabel,
  IonThumbnail,
  IonImg,
  IonList,
  IonItemSliding,
  IonText,
  IonItemOptions,
  IonItemOption,
  IonCard,
  IonCheckbox,
} from '@ionic/react';
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { add as addIcon, handLeft } from 'ionicons/icons';
import { formatDate } from '../date';
import { trashOutline } from 'ionicons/icons';

interface EntryItemProps {
  entry: Entry;
  onCompleted: (id: string, completed: boolean) => void;
}

const HomePage: React.FC = () => {
  const { userId } = useAuth();
  const [ entries, setEntries ] = useState<Entry[]>([]);
  const [completed, setCompleted] = useState(entry.completed);
  // const entryRef = firestore.collection('entries').doc(id);
  // entryRef.get().then((doc) => setEntry(toEntry(entry)));
  const handleCheckboxChange = (event: CustomEvent) => {
    const newCompleted = event.detail.checked;
    setCompleted(newCompleted);
    onCompleted(entry.id, newCompleted);
  };


  useEffect(() => {
      const entriesRef = firestore.collection('users').doc(userId)
        .collection('entries');
      return entriesRef.orderBy('date','desc').limit(7)
        .onSnapshot(({docs}) => setEntries(docs.map(toEntry)))
      //  entriesRef.get().then(({docs}) => setEntries(docs.map(toEntry)))
  }, [userId]);


  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Homepage</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent>
      <IonCard>
        {entries.map((entry) =>

          <IonCard button key={entry.id}
            routerLink={`/my/entries/view/${entry.id}`}>
            <IonItem>
            <IonCheckbox slot='start' checked={completed} onIonChange={handleCheckboxChange} />
            <IonText>
            <h2>{entry.title}</h2>
            <h6>{formatDate(entry.date)}</h6>
            </IonText>
            </IonItem>
          </IonCard>
        )}
        </IonCard>
        <IonFab vertical="bottom" horizontal="end">
            <IonFabButton routerLink="/my/entries/add">
              <IonIcon icon={addIcon} />
            </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default HomePage;
