import {
  IonContent,
  IonHeader,
  IonPage,
  IonTitle,
  IonToolbar,
  IonButton,
  IonInput,
  IonLabel,
  IonTextarea
} from '@ionic/react';
import React, { useState } from 'react';
import { useHistory } from 'react-router';
import { useAuth } from '../auth';
import { firestore } from '../firebase';
import { Entry } from '../models';

const AddEntryPage: React.FC = () => {
  const { userId } = useAuth();
  const history = useHistory();
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [pictureUrl, setPictureUrl] = useState('');

  const handleSave = async () => {
    const entryRef = firestore.collection('users').doc(userId)
      .collection('entries').doc();
    const newEntry: Entry = {
      id: entryRef.id,
      title,
      description,
      date: new Date(date),
      pictureUrl
    };
    await entryRef.set(newEntry);
    setTitle('');
    setDescription('');
    setDate('');
    setPictureUrl('');
    history.push(`/entry/${newEntry.id}`);
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Add Entry</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        <IonLabel position="stacked">Title</IonLabel>
        <IonInput
          value={title}
          onIonChange={(e) => setTitle(e.detail.value!)}
        />
        <IonLabel position="stacked">Description</IonLabel>
        <IonTextarea
          value={description}
          onIonChange={(e) => setDescription(e.detail.value!)}
        />
        <IonLabel position="stacked">Date</IonLabel>
        <IonInput
          type="date"
          value={date}
          onIonChange={(e) => setDate(e.detail.value!)}
        />
        <IonLabel position="stacked">Picture URL</IonLabel>
        <IonInput
          value={pictureUrl}
          onIonChange={(e) => setPictureUrl(e.detail.value!)}
        />
        <IonButton expand="block" onClick={handleSave}>
          Add Entry
        </IonButton>
      </IonContent>
    </IonPage>
  );
};

export default AddEntryPage;
