import {
  IonContent,
  IonFab,
  IonFabButton,
  IonHeader,
  IonIcon,
  IonItem,
  IonPage,
  IonTitle,
  IonToolbar,
  IonLabel,
  IonThumbnail,
  IonImg,
  IonList,
  IonItemSliding,
  IonText,
  IonItemOptions,
  IonItemOption,
} from '@ionic/react';
import React, { useEffect, useState } from 'react';
import { useAuth } from '../auth';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { add as addIcon } from 'ionicons/icons';
import { formatDate } from '../date';

const HomePage: React.FC = () => {
  const { userId } = useAuth();
  const [ entries, setEntries ] = useState<Entry[]>([]);
  // const entryRef = firestore.collection('entries').doc(id);
  // entryRef.get().then((doc) => setEntry(toEntry(entry)));

  useEffect(() => {
      const entriesRef = firestore.collection('users').doc(userId)
        .collection('entries');
      return entriesRef.orderBy('date','desc').limit(7)
        .onSnapshot(({docs}) => setEntries(docs.map(toEntry)))
      //  entriesRef.get().then(({docs}) => setEntries(docs.map(toEntry)))
  }, [userId]);

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Homepage</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
      <IonList>
        {entries.map((entry) =>
          <IonItemSliding>
          <IonItem button key={entry.id}
            routerLink={`/my/entries/view/${entry.id}`}>
            <IonThumbnail slot="end">
              <IonImg src={entry?.pictureUrl}/>
            </IonThumbnail>
            <IonText>
              <h1>{formatDate(entry.date)}</h1>
              <h2>{entry.title}</h2>
            </IonText>
          </IonItem>
          <IonItemOptions>
            <IonItemOption>
              <IonIcon slot='icon-only' icon={traskOutline} />
            </IonItemOption>
          </IonItemOptions>
          </IonItemSliding>
        )}
        </IonList>
        <IonFab vertical="bottom" horizontal="end">
            <IonFabButton routerLink="/my/entries/add">
              <IonIcon icon={addIcon} />
            </IonFabButton>
        </IonFab>
      </IonContent>
    </IonPage>
  );
};

export default HomePage;
