import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import './calendarstyle.css';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const EntriesContainer = styled.div`
  margin-top: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const EntryItem = styled.div`
  border: 1px solid gray;
  padding: 5px;
  margin: 5px;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');

    // Listen to changes in the collection and update the entries state
    const unsubscribe = entriesRef.onSnapshot(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });

    return unsubscribe; // Cleanup the listener when the component unmounts

  }, [userId]);

  const events = entries.map((entry) => ({
    title: entry.title,
    date: new Date(entry.date),
    id: entry.id,
    classNames: entry.completed ? 'completed' : 'not-completed'
  }));

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`);
    setEntriesList(
      <EntryItem key={event.id}>
        <div>{event.title}</div>
        <div>{new Date(event.date).toLocaleString()}</div>
        <div>Completed: {event.classNames === 'completed' ? 'Yes' : 'No'}</div>
      </EntryItem>
    );
  };

  const handleDateClick = (arg: any) => {
    const selectedDate = arg.date;
    const selectedEntries = entries.filter(
      entry => new Date(entry.date).toDateString() === selectedDate.toDateString()
    );

    const entriesList = selectedEntries.map((entry) => (
      <EntryItem key={entry.id}>
        <div>{entry.title}</div>
        <div>{new Date(entry.date).toLocaleString()}</div>
        <div>Completed: {entry.completed ? 'Yes' : 'No'}</div>
      </EntryItem>
    ));

    setEntriesList(entriesList);
  };

  const [entriesList, setEntriesList] = useState<JSX.Element[]>([]);

  return (
    <CalendarContainer>
      {!loading && (
        <>
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            events={events}
            eventClick={handleEventClick}
            dateClick={handleDateClick} // attach the dateClick event handler
            eventColor="#378006"
            height='500px'
          />

          <EntriesContainer>
            {entriesList.length > 0 ? entriesList : <div>Select a date to see entries</div>}
          </EntriesContainer>
        </>
      )}
    </CalendarContainer>
  );
};

export default Calendar;
