import {
    IonContent,
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonBackButton,
    IonButtons,
    IonButton,
    IonInput,
    IonLabel,
    IonTextarea,
    IonItem,
    IonDatetime
  } from '@ionic/react';
  import React, { useEffect, useState } from 'react';
  import { useHistory, useLocation, useParams } from 'react-router';
  import { useAuth } from '../auth';
  import { firestore } from '../firebase';
  import { Entry, toEntry } from '../models';
  import { formatDate } from '../date';

  interface RouteParams {
    id: string;
  }

  const EditEntryPage: React.FC = () => {
    const { id } = useParams<RouteParams>();
    const { userId } = useAuth();
    const history = useHistory();
    const location = useLocation<{ entry: Entry }>();
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('');
    const [pictureUrl, setPictureUrl] = useState('');

    useEffect(() => {
      if (location.state?.entry) {
        const { title, description, date, pictureUrl } = location.state.entry;
        setTitle(title);
        setDescription(description);
        setDate(formatDate(date));
        setPictureUrl(pictureUrl);
      } else {
        const entryRef = firestore.collection('users').doc(userId)
          .collection('entries').doc(id);
        entryRef.get().then((doc) => {
          const { title, description, date, pictureUrl } = toEntry(doc);
          setTitle(title);
          setDescription(description);
          setDate(formatDate(date));
          setPictureUrl(pictureUrl);
        });
      }
    }, [id, location.state?.entry, userId]);

    const handleSave = async () => {
      const entryRef = firestore.collection('users').doc(userId)
        .collection('entries').doc(id);
      const updatedEntry = {
        title,
        description,
        date: new Date(date),
        pictureUrl
      };
      await entryRef.set(updatedEntry, { merge: true });
      history.replace(`m/entry/${id}`);
    };

    return (
      <IonPage>
        <IonHeader>
          <IonToolbar>
            <IonButtons slot="start">
              <IonBackButton />
            </IonButtons>
            <IonTitle>{location.state?.entry ? 'Edit Entry' : 'Add Entry'}</IonTitle>
            <IonButtons slot="end">
              <IonButton onClick={handleSave}>
                Save
              </IonButton>
            </IonButtons>
          </IonToolbar>
        </IonHeader>
        <IonContent className="ion-padding">
          <IonItem>
            <IonLabel position="floating">Title</IonLabel>
            <IonInput
              value={title}
              onIonChange={(e) => setTitle(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Description</IonLabel>
            <IonTextarea
              value={description}
              onIonChange={(e) => setDescription(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Date</IonLabel>
            <IonDatetime
              display-format="MMM DD, YYYY HH:mm"
              picker-format="MMM DD YYYY HH:mm"
              value={date}
              onIonChange={(e) => setDate(e.detail.value!)}
            />
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Picture URL</IonLabel>
            <IonInput
              value={pictureUrl}
              onIonChange={(e) => setPictureUrl(e.detail.value!)}
            />
            </IonItem>
      </IonContent>
    </IonPage>
  );
};

export default EditEntryPage;
