import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [events, setEvents] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');
    entriesRef.get().then(({ docs }) => {
      const newEntries = docs.map(toEntry);
      setEntries(newEntries);
      setLoading(false);
    });
  }, [userId]);

  useEffect(() => {
    const unsubscribe = firestore
      .collection('users')
      .doc(userId)
      .collection('entries')
      .onSnapshot((snapshot) => {
        const newEntries = snapshot.docs.map(toEntry);
        setEntries(newEntries);
      });
    return unsubscribe;
  }, [userId]);

  useEffect(() => {
    const newEvents = entries.map((entry) => ({
      title: entry.title,
      date: new Date(entry.date),
      id: entry.id,
    }));
    setEvents(newEvents);
  }, [entries]);

  const handleEventClick = ({ event }: { event: any }) => {
    history.push(`/my/entries/view/${event.id}`);
  };

  return (
    <CalendarContainer>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <FullCalendar
          plugins={[dayGridPlugin]}
          initialView="dayGridMonth"
          events={events}
          eventClick={handleEventClick}
          lazyFetching={true}
          height="500px"
        />
      )}
    </CalendarContainer>
  );
};

export default Calendar;
