import React, { useState, useEffect } from 'react';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import { firestore } from '../firebase';
import { Entry, toEntry } from '../models';
import { useAuth } from '../auth';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import './calendarstyle.css';

const CalendarContainer = styled.div`
  font-size: 0.8rem;
`;

const CardContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  margin-top: 20px;
`;

const Card = styled.div`
  background-color: #fff;
  box-shadow: 0 2px 3px rgba(10, 10, 10, 0.1);
  margin: 10px;
  padding: 10px;
  width: 200px;
`;

const Calendar: React.FC = () => {
  const { userId } = useAuth();
  const [entries, setEntries] = useState<Entry[]>([]);
  const [loading, setLoading] = useState(true);
  const history = useHistory();
  const [selectedDate, setSelectedDate] = useState<Date>();

  useEffect(() => {
    const entriesRef = firestore
      .collection('users')
      .doc(userId)
      .collection('entries');

    // Listen to changes in the collection and update the entries state
    const unsubscribe = entriesRef.onSnapshot(({ docs }) => {
      setEntries(docs.map(toEntry));
      setLoading(false);
    });

    return unsubscribe; // Cleanup the listener when the component unmounts

  }, [userId]);

  const handleDayClick = (arg: any) => {
    setSelectedDate(arg.date);
  };

  const options = {
    dayClick: handleDayClick
  };

  const filteredEntries = entries.filter(
    (entry) =>
      selectedDate &&
      new Date(entry.date).toLocaleDateString() === selectedDate.toLocaleDateString()
  );

  const entryCards = filteredEntries.map((entry) => (
    <Card key={entry.id}>
      <h3>{entry.title}</h3>
      <p>{new Date(entry.date).toLocaleString()}</p>
      <button onClick={() => history.push(`/my/entries/view/${entry.id}`)}>
        View
      </button>
    </Card>
  ));


  return (
    <CalendarContainer>
      {!loading && (
        <div>
          <FullCalendar
            plugins={[dayGridPlugin]}
            initialView="dayGridMonth"
            events={entries.map((entry) => ({
              title: entry.title,
              date: new Date(entry.date),
              id: entry.id,
              classNames: entry.completed ? 'completed' : 'not-completed'
            }))}
            eventClick={({ event }) =>
              history.push(`/my/entries/view/${event.id}`)
            }
            options={options} // Add options prop here
            />
          {selectedDate && (
            <>
              <h2>Entries for {selectedDate.toLocaleDateString()}</h2>
              <CardContainer>{entryCards}</CardContainer>
            </>
          )}
        </div>
      )}
    </CalendarContainer>
  );
};

export default Calendar;
