  // }
  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Homepage</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        {entries.map((entry) =>
          <IonItem button key={entry.id}
            routerLink={`/my/entries/${entry.id}`}>
            {entry.title}
          </IonItem>
        )}
      </IonContent>
    </IonPage>
  );
};

